const addLikeHandler = (c) => {
    if (!likePressed) {
        setCount(count + 1);
        updatePlusScoreHandler(count + 1);
        setLikePressed(true);
    } else {
        setCount(count - 1);
        updatePlusScoreHandler(count - 1);
        setLikePressed(false);
        if (likePressed === true && dislikePressed === true) {
            setCount(count + 1);
            setLikePressed(true);
            setDislikePressed(false);
        }
    }
};
export default addLikeHandler;
