import React, { useEffect, useState } from 'react';
import Comment from './comment';
import Replie from './reply';
import SendComment from './send-comment';
import Delete from './widget/delete';
import { selectOverlay } from '../features/dataSlice';
import { useSelector } from 'react-redux';

function Comments() {
	const [ data, setData ] = useState([]);
	//const [overlay, setOverlay] = useState()
	const [replyToDelete, setReplyToDelete] = useState();
	const overlay = useSelector(selectOverlay)

	useEffect(() => {
		const defaultData = async () => {
			const res = await fetch('http://localhost:3001/comments');
			const data = await res.json();
			console.log('data front',data)
			setData(data);
		};
		defaultData();
	}, [data]);

	//const overlayHandler = () => {
	//	console.log('toggleOverlay')
	//	setOverlay(toggleOverlay);
	//}


	return (
		<main>
			<div className="pt-8 pr-4 pb-8 pl-4 z-10">
			<div className="mb-4">
				{data?.map(item => (	
					<div>
						<Comment key={item.id} idComment={item.id} usernameComment={item.user.username}  createdAtComment={item.createdAt} contentComment={item.content}  scoreComment={item.score} buttonText="Reply" png={item.user.image.png} webp={item.user.image.webp}/>
						{item?.replies && (
            				<div>
              					{item?.replies.map((replie) => (
									<Replie key={replie.id} idReplie={replie.id} idComment={item.id} usernameReplie={replie.user.username} createdAtReplie={replie.createdAt} scoreReplie={replie.score} png={replie.user.image.png} webp={replie.user.image.webp} contentReplie={replie.content} buttonText="Reply" replyingTo={replie.user.username}  />
             					 ))}
            				</div>

         				 )}
					</div>	
				))}
			</div>
			<SendComment buttonText="SEND"/>
			<div>
			</div>
			{overlay ? <div className='bg-overlay z-30 w-full h-screen top-0 right-0 fixed  pl-4 pr-4 flex justify-center items-center'><Delete /></div>:null}
		</div>
		</main>
	);
								}

export default Comments;
