import React, { useState, useEffect } from 'react';
import SendComment from './send-comment';
import Moment from 'react-moment';

function Comment({ idComment, usernameComment, createdAtComment, contentComment, image,buttonText, scoreComment }) {
	const [ count, setCount ] = useState(scoreComment);
	const [screenSize, setScreenSize] = useState('');
	const [open, setOpen] = useState(false);
	const [ error, setError ] = useState();

	useEffect(() => {
		const handleResize = () => {
			if (window.innerWidth < 640) {
			  setScreenSize('sm');
			} else if (window.innerWidth < 768) {
			  setScreenSize('md');
			}
		}
			handleResize();
    window.addEventListener('resize', handleResize);
    //return () => window.removeEventListener('resize', handleResize);
	}, [])

	const addLikeHandler = () => {
		setCount(() => count + 1);
	};
	const minusLikeHandler = () => {
		if(count > 0) {
			setCount(() => count - 1);
		}
		else {
			setCount(0)
		}
	};

	const replyHandler = async() => {
		setOpen(true);


	}

	return (
		<>
		<div className={`${screenSize === "sm"} ? console.log('small):`}>

		</div>
		<div className="p-4 flex flex-col bg-component-background rounded-lg ">
			<div className="h-8 mb-5 rounded-full bg-red-500 flex flex-row justify-start items-center">
				<div className="w-8 h-8 rounded-full mr-4">
					<img src={image} alt="avatar" />
				</div>
				<h1 className="m-8 text-dark-blue text-base font-medium">{usernameComment}</h1>
				<h2 className="text-grayish-blue text-base font-normal"><Moment fromNow>{createdAtComment}</Moment></h2>
			</div>
			<div className="text-grayish-blue text-base font-normal mb-4">{contentComment}</div>
			<div className=" flex flex-row justify-between ">
				<div className="bg-very-light-gray flex flex-row pt-2.5 pr-[35px] pb-2.5 pl-[35px] space-x-3.5 rounded-lg ">
					<button className="text-light-grayish-blue" onClick={addLikeHandler}>
						+
					</button>
					<p className="text-moderate-blue text-base font-medium">{count}</p>
					<button className="text-light-grayish-blue " onClick={minusLikeHandler}>
						-
					</button>
				</div>


				<div className="flex flex-row items-center text-moderate-blue cursor-pointer " onClick={replyHandler}>
					<svg width="14" height="13" xmlns="http://www.w3.org/2000/svg">
						<path
							d="M.227 4.316 5.04.16a.657.657 0 0 1 1.085.497v2.189c4.392.05 7.875.93 7.875 5.093 0 1.68-1.082 3.344-2.279 4.214-.373.272-.905-.07-.767-.51 1.24-3.964-.588-5.017-4.829-5.078v2.404c0 .566-.664.86-1.085.496L.227 5.31a.657.657 0 0 1 0-.993Z"
							fill="#5357B6"
						/>
					</svg>
					<h2 className="ml-2 font-medium">Reply</h2>
				</div>
			</div>

		</div>
		<div className="mb-4 pl-4">
			{console.log("kfkfkjfkjgkfjgkf", idComment,usernameComment,contentComment)}
{open ? <SendComment buttonText={buttonText} idComment={idComment} usernameComment={usernameComment} contentComment={contentComment} createdAt={createdAtComment} image={image} score={scoreComment}/> :null}
		</div>
		</>
	);
}

export default Comment;
