import React, { useState, useEffect } from 'react';
import SendComment from './send-comment';
import ReplySmallScreen from './comment/reply-small-screen';
import Moment from 'react-moment';
import { useDispatch } from 'react-redux';
import { toggleOverlay, setCommentId, setReplyId } from '../features/dataSlice';
import addHandler from '../utils/addHandler';
import minusHandler from '../utils/minusHandler';
import dayjs from 'dayjs';
import relativeTime from 'dayjs/plugin/relativeTime';


function Reply({
	idComment,
	idReply,
	usernameComment,
	usernameReply,
	commentReply,
	createdAtReply,
	image,
	contentReply,
	buttonText,
	scoreReply,
	replyingTo,
	png,
	webp
}) {
	const [ count, setCount ] = useState(scoreReply);
	const [ open, setOpen ] = useState(false);
	const [ screenSize, setScreenSize ] = useState('');
	const [ likePressed, setLikePressed ] = useState(false);
	const [ dislikePressed, setDislikePressed ] = useState(false);
	const dispatch = useDispatch();
	const [ edit, setEdit ] = useState(false);
	const [ inputField, setInputField ] = useState(contentReply);
	const [ data, setData ] = useState();
	const [ error, setError ] = useState();
	dayjs.extend(relativeTime)
	const diffInDays = dayjs(createdAtReply).fromNow();


	useEffect(() => {
		const handleResize = () => {
			if (window.innerWidth < 640) {
				setScreenSize('sm');
			} else if (window.innerWidth > 768) {
				setScreenSize('md');
			}
		};
		handleResize();
		window.addEventListener('resize', handleResize);
	}, []);

	const addLikeHandler = () => {
		addHandler(count, likePressed, setCount, updateScoreHandler, setLikePressed, dislikePressed, setDislikePressed);
	};

	const minusLikeHandler = () => {
		minusHandler(
			dislikePressed,
			setCount,
			count,
			updateScoreHandler,
			setDislikePressed,
			likePressed,
			setLikePressed
		);
	};

	const replyHandler = () => {
		setOpen(true);
	};

	const updateScoreHandler = async (updateData) => {
		try {
			const response = await fetch(`http://localhost:3001/comments/${idComment}/replies/${idReply}/score`, {
				method: 'PATCH',
				headers: {
					'Content-Type': 'application/json'
				},

				body: JSON.stringify({
					score: updateData
				})
			});
			const responseData = await response.json();
			setData(responseData);

			if (!response.ok) {
				throw new Error(responseData.message);
			}
		} catch (err) {
			setError(err.message || 'Something went wrong');
			console.log(err);
		}
	};

	const deleteWidgetHandler = () => {
		dispatch(toggleOverlay(true));
		dispatch(setCommentId(idComment));
		dispatch(setReplyId(idReply));
	};
	const editHandler = async () => {
		setEdit(true);
	};
	const inputHandler = (e) => {
		setInputField(e.target.value);
	};
	const updateHandler = async () => {
		try {
			const response = await fetch(`http://localhost:3001/comments/${idComment}/replies/${idReply}`, {
				method: 'PATCH',
				headers: {
					'Content-Type': 'application/json'
				},

				body: JSON.stringify({
					content: inputField
				})
			});
			const responseData = await response.json();
			setData(responseData);

			if (!response.ok) {
				throw new Error(responseData.message);
			}
		} catch (err) {
			setError(err.message || 'Something went wrong');
			console.log(err);
		}
		setEdit(false);
	};

	return (
		<div>
			<div className="mb-4 pl-4 xl:pl-[43px]">
				{screenSize === 'sm' ? (
					<ReplySmallScreen diffInDays={diffInDays} idComment={idComment} idReply={idReply} usernameComment={usernameComment} commentReply={commentReply} createdAtReply={createdAtReply} contentReply={contentReply} buttonText={buttonText} scoreReply={scoreReply} replyingTo={replyingTo} png={png} webp={webp}  />
				) : (
					<ReplyLScreen diffInDays={diffInDays} idComment={idComment} idReply={idReply} usernameComment={usernameComment} commentReply={commentReply} createdAtReply={createdAtReply} contentReply={contentReply} buttonText={buttonText} scoreReply={scoreReply} replyingTo={replyingTo} png={png} webp={webp}  />
				)}

			</div>
		</div>
	);
}

export default Reply;
