import React, { useState, useEffect } from 'react';
import SendComment from './send-comment';
import { useDispatch } from 'react-redux';
import { toggleOverlay, setCommentId } from '../features/dataSlice';
import addHandler from '../utils/addHandler';
import minusHandler from '../utils/minusHandler';
import dayjs from 'dayjs';
import relativeTime from 'dayjs/plugin/relativeTime';
import CommentSmallScreen from './comment/comment-small-screen';
import CommentLargeScreen from './comment/comment-small-screen';

function Comment({
	idComment,
	usernameComment,
	createdAtComment,
	contentComment,
	png,
	webp,
	buttonText,
	scoreComment
}) {

	const [ screenSize, setScreenSize ] = useState('');
	const [ likePressed, setLikePressed ] = useState(false);
	const [ dislikePressed, setDislikePressed ] = useState(false);
	const [ open, setOpen ] = useState(false);
	const [ data, setData ] = useState();
	const [ error, setError ] = useState();
	const [ edit, setEdit ] = useState(false);
	const [ inputField, setInputField ] = useState(contentComment);
	const dispatch = useDispatch();
	dayjs.extend(relativeTime);
	const diffInDays = dayjs(createdAtComment).fromNow();

	useEffect(() => {
		const handleResize = () => {
			if (window.innerWidth < 640) {
				setScreenSize('sm');
			} else if (window.innerWidth > 768) {
				setScreenSize('md');
			}
		};
		handleResize();
		window.addEventListener('resize', handleResize);
	}, []);



	return (
		<main>
			{screenSize === 'sm' ? (
				<CommentSmallScreen
					png={png}
					usernameComment={usernameComment}
					diffInDays={diffInDays}
					scoreComment={scoreComment}
					idComment={idComment}
					contentComment={contentComment}
					buttonText={buttonText}
					createdAtComment={createdAtComment}
					webp={webp}
				/>
			) : (
				<CommentLargeScreen
					png={png}
					usernameComment={usernameComment}
					diffInDays={diffInDays}
					scoreComment={scoreComment}
					idComment={idComment}
					contentComment={contentComment}
					buttonText={buttonText}
					createdAtComment={createdAtComment}
					webp={webp}
				/>
			)}
		</main>
	);
}

export default Comment;
