const minusHandler = (dislikePressed,setCount,count,updatePlusScoreHandler,setDislikePressed,likePressed,setLikePressed) => {
    if (!dislikePressed) {
        setCount(() => count - 1);
        updatePlusScoreHandler(count - 1);
        setDislikePressed(true);
    } else {
        setCount(() => count + 1);
        updatePlusScoreHandler(count + 1);
        setDislikePressed(false);

        if (likePressed === true && dislikePressed === true) {
            setCount(count - 1);
            setLikePressed(false);
            setDislikePressed(true);
        }
    }
};

export default minusHandler;
