import React, { useEffect, useState } from 'react';
import Comment from './comment';
import Replie from './reply';
import SendComment from './send-comment';
import Delete from './widget/delete';
import { selectOverlay } from '../features/dataSlice';
import { useSelector } from 'react-redux';

function Comments() {
	const [ data, setData ] = useState([]);
	const [overlay, setOverlay] = useState()
	const [replyToDelete, setReplyToDelete] = useState();
	const toggleOverlay = useSelector(selectOverlay)

	useEffect(() => {
		const defaultData = async () => {
			const res = await fetch('http://localhost:3001/comments');
			const data = await res.json();
			console.log('data front',data)
			setData(data);
		};
		defaultData();
	}, []);

	//const overlayHandler = () => {
	//	console.log('toggleOverlay')
	//	setOverlay(toggleOverlay);
	//}


	return (
		<>
		<div className="pt-8 pr-4 pb-8 pl-4 z-10">
			<div className="mb-4">
				{data?.map(item => (	
					<>
						<Comment key={item.id} idComment={item.id} usernameComment={item.user.username}  createdAtComment={item.createdAt} contentComment={item.content}  scoreComment={item.score} buttonText="Reply"/>
						{item?.replies && (
            				<div>
              					{item?.replies.map((replie) => (
									<Replie key={replie.id} idReplie={replie.id} idComment={item.id} usernameReplie={replie.user.username} createdtReplie={replie.createdAt} scoreReplie={replie.score} image={replie.image} contentReplie={replie.content} buttonText="Reply" overlayHandler={overlayHandler} />
             					 ))}
            				</div>

         				 )}
					</>	
				))}
			</div>
			<SendComment buttonText="SEND"/>
			<div>
			</div>
			{overlay ? <div className='bg-overlay z-30 w-full h-screen top-0 right-0 fixed  pl-4 pr-4 flex justify-center items-center'><Delete overlayHandler={overlayHandler} /></div>:null}



		</div>
		</>
	);
								}

export default Comments;
