import './App.css';

import Comments from './components/comments';

function App() {
	return (
		<>
		<div className="bg-background font-Rubik">
			<Comments />
		</div>
		<div class="w-full h-full flex  justify-center items-center bg-blue-600/30 backdrop-brightness-75">
		<span class="text-white text-4xl w-1/2 text-center">Here is an example of colored overlay on an image</span>
</div>
		</>

	);
}

export default App;
