import React, { useState, useEffect } from 'react';
import SendComment from './send-comment';
import { useDispatch } from 'react-redux';
import { toggleOverlay, setCommentId } from '../features/dataSlice';
import addHandler from '../utils/addHandler';
import minusHandler from '../utils/minusHandler';
import dayjs from 'dayjs';
import relativeTime from 'dayjs/plugin/relativeTime';
import CommentSmallScreen from './comment/comment-small-screen';
import CommentLargeScreen from './comment/comment-small-screen';

function Comment({
	idComment,
	usernameComment,
	createdAtComment,
	contentComment,
	png,
	webp,
	buttonText,
	scoreComment
}) {
	const [ count, setCount ] = useState(scoreComment);
	const [ screenSize, setScreenSize ] = useState('');
	const [ likePressed, setLikePressed ] = useState(false);
	const [ dislikePressed, setDislikePressed ] = useState(false);
	const [ open, setOpen ] = useState(false);
	const [ data, setData ] = useState();
	const [ error, setError ] = useState();
	const [ edit, setEdit ] = useState(false);
	const [ inputField, setInputField ] = useState(contentComment);
	const dispatch = useDispatch();
	dayjs.extend(relativeTime)
	const diffInDays = dayjs(createdAtComment).fromNow();

	useEffect(() => {
		const handleResize = () => {
			if (window.innerWidth < 640) {
				setScreenSize('sm');
			} else if (window.innerWidth > 768) {
				setScreenSize('md');
			}
		};
		handleResize();
		window.addEventListener('resize', handleResize);
	}, []);

	const addLikeHandler = () => {
		addHandler(count, likePressed, setCount, updateScoreHandler, setLikePressed, dislikePressed, setDislikePressed);
	};

	const minusLikeHandler = () => {
		minusHandler(
			dislikePressed,
			setCount,
			count,
			updateScoreHandler,
			setDislikePressed,
			likePressed,
			setLikePressed
		);
	};

	const updateScoreHandler = async (updateData) => {
		try {
			const response = await fetch(`http://localhost:3001/comments/${idComment}/score`, {
				method: 'PATCH',
				headers: {
					'Content-Type': 'application/json'
				},

				body: JSON.stringify({
					score: updateData
				})
			});
			const responseData = await response.json();
			setData(responseData);
			if (!response.ok) {
				throw new Error(responseData.message);
			}
		} catch (err) {
			setError(err.message || 'Something went wrong');
			console.log(err);
		}
	};

	const replyHandler = async () => {
		setOpen(true);
	};
	const editHandler = async () => {
		setEdit(true);
	};
	const inputHandler = (e) => {
		setInputField(e.target.value);
	};
	const updateHandler = async () => {
		try {
			const response = await fetch(`http://localhost:3001/comments/${idComment}`, {
				method: 'PATCH',
				headers: {
					'Content-Type': 'application/json'
				},
				body: JSON.stringify({
					content: inputField
				})
			});
			const responseData = await response.json();
			setData(responseData);

			if (!response.ok) {
				throw new Error(responseData.message);
			}
		} catch (err) {
			setError(err.message || 'Something went wrong');
			console.log(err);
		}
		setEdit(false);
	};

	const deleteWidgetHandler = () => {
		dispatch(toggleOverlay(true));
		dispatch(setCommentId(idComment));
	};

	return (
		<main>
			{screenSize === 'sm' ? (
		<CommentSmallScreen png={png} usernameComment={usernameComment} diffInDays={diffInDays} scoreComment={scoreComment} idComment={idComment} contentComment={contentComment} buttonText={buttonText} createdAtComment={createdAtComment} webp={webp}/>
		) : (
			<CommentLargeScreen png={png} usernameComment={usernameComment} diffInDays={diffInDays} scoreComment={scoreComment} idComment={idComment} contentComment={contentComment} buttonText={buttonText} createdAtComment={createdAtComment} webp={webp}/>
		)
}
		</main>
	);
}

export default Comment;
