import React from 'react';
import { useState, useEffect, useRef } from 'react';
//import axios from 'axios';

function SendComment({ buttonText, id, name,username,createdAt,score,image}) {
	const [ comment, setComment ] = useState('Add comment...');
	const [ error, setError ] = useState();
	const [ ids, setIds ] = useState(1);
	const defaultUsername = 'juliusomo';

	//const [ imgPng, setImgPng ] = useState('http://localhost:3000/images/avatars/image-juliusomo.png');
	//const [ imgWebp, setImgWebp ] = useState('http://localhost:3000/images/avatars/image-juliusomo.webp');
	//const [ base64String, setBase64String ] = useState();

	const textAreaHandler = () => {
		setComment('');
	};
	const textAreaUserInputHandler = (e) => {
		setComment(e.target.value);
	};
	const commentSubmitHandler = async (e) => {
		//const formData = new FormData();

		//formData.append('content', comment);
		//formData.append('createdAt', '1 month ago');
		//formData.append('score', '30');
		//formData.append('png', {
		//	url: imgPng.url,
		//	type: imgPng.type,
		//	name: imgPng.name
		//});
		//formData.append('webp', {
		//	url: imgWebp.url,
		//	type: imgWebp.type,
		//	name: imgWebp.name
		//});
		//formData.append('username', 'juliusomo');

		//const result = await axios.post('http://localhost:3001/comments', formData, { headers: {'Content-Type': 'multipart/form-data'}})
		if (buttonText === 'SEND') {
			try {
				const response = await fetch('http://localhost:3001/comments', {
					method: 'POST',
					headers: {
						'Content-Type': 'application/json'
					},
					body: JSON.stringify({
						content: comment,
						username: defaultUsername
					})
				});
				console.log("ys;l;l;;;;e")
				const responseData = await response.json();
				//console.log("response",responseData);
				if (!response.ok) {
					throw new Error(responseData.message);
				}
				else {
					try {
						const response = await fetch(`http://localhost:3001/comments/${id}/replies`, {
							method: 'POST',
							headres: {
								'Content-Type': 'application/json'
							},
							body: {
								content: comment,
								replyingTo: name,
								username: de
							}
						})
					}
					catch(err) {
						setError(err.message || 'Something went wrong');
							console.log(err);
					}
				}


			} catch (err) {
				setError(err.message || 'Something went wrong');
				console.log(err);
			}
			//setIds(() => ids + 1);
		} 
		e.preventDefault();
	};

	return (
		<div className="p-4 flex flex-col bg-component-background rounded-lg">
			<form>
				<textarea
					onClick={textAreaHandler}
					onChange={textAreaUserInputHandler}
					value={comment}
					className="focus:outline-none border-[1px] pt-3 pl-6 h-24 mb-3.5 text-base font-normal text-grayish-blue leading-6 border-light-gray rounded-md w-[100%] resize-none"
				/>
				<div className="flex flex-row justify-between items-center">
					<div className="w-8 h-8 rounded-full">
						<img src="../images/avatars/image-juliusomo.png" alt="user-avatar" />
					</div>
					<button
						type="submit"
						onClick={commentSubmitHandler}
						className="text-base text-white  leading-6 uppercase rounded-md bg-moderate-blue pt-3 pl-[30px] pr-[30px] pb-3"
					>
						{buttonText}
					</button>
				</div>
			</form>
		</div>
	);
}

export default SendComment;
