import React, { useEffect, useState } from 'react';
import Comment from './comment';
import Replie from './reply';
import SendComment from './send-comment';

function Comments() {
	const [ data, setData ] = useState([]);
	const [overlay, setOverlay] = useState()
	useEffect(() => {
		const defaultData = async () => {
			const res = await fetch('http://localhost:3001/comments');
			const data = await res.json();
			console.log('data front',data)
			setData(data);
		};
		defaultData();
	}, []);

	const deleteHandler = (overlayDelete) => {
		setOverlay(overlayDelete)
		console.log('overlay',overlay)
	}

	return (
		<div className="pt-8 pr-4 pb-8 pl-4">
			<div className="mb-4">
				{data?.map(item => (	
					<>
						<Comment key={item.id} idComment={item.id} usernameComment={item.user.username}  createdAtComment={item.createdAt} contentComment={item.content}  scoreComment={item.score} buttonText="Reply"/>
						{item?.replies && (
            				<div>
              					{item?.replies.map((replie) => (
									<Replie key={replie.id} idReplie={replie.id} idComment={item.id} usernameReplie={replie.user.username} createdtReplie={replie.createdAt} scoreReplie={replie.score} image={replie.image} contentReplie={replie.content} buttonText="Reply" onData={deleteHandler} />
             					 ))}
            				</div>

         				 )}
					</>	
				))}
			</div>
			<SendComment buttonText="SEND"/>
			<div>
			</div>
			{overlay ? <div className='bg-graish-blue w-[100%'></div>: null}
		</div>
	);
}

export default Comments;
