import React, { useEffect, useState } from 'react';
import Comment from './comment';
import Replie from './reply';
import SendComment from './send-comment';

function Comments() {
	const [ data, setData ] = useState([]);
	let buttonText;
	useEffect(() => {
		const defaultData = async () => {
			const res = await fetch('http://localhost:3001/comments');
			const data = await res.json();
			console.log('data front',data)
			setData(data);
		};
		defaultData();
	}, []);
	return (
		<div className="pt-8 pr-4 pb-8 pl-4">
			<div className="mb-4">
				{console.log('data front',data)}
			{data?.map(item => {

				//const imagePath = "http://localhost:3001/uploads/" +`${item.png}`
				return <Comment key={item.id} id={item.id} name={item.username}  createdAt={item.createdAt} content={item.content}  score={item.score} buttonText="Reply" />
					{console.log('replies',item.replies)}
					if (item?.replies.length > 0) {
						console.log('wieksze')
						item.replies.map(replie => {
							return (
								<>
								<Comment key={item.id} id={item.id} name={item.username}  createdAt={item.createdAt} content={item.content}  score={item.score} buttonText="Reply" />
								<Replie key={replie.id} username={replie.username} createdt={replie.createdAt} score={replie.score} image={replie.image} content={replie.content} />

								</>)

						})
					}
				})}
				{console.log(data)}
			</div>
			<SendComment buttonText="SEND"/>
			<div>
			</div>
		</div>
	);
}

export default Comments;
