import { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { toggleOverlay, setCommentId, set} from '../../features/dataSlice'

function Delete({overlayHandler}) {
    const dispatch = useDispatch();
    const id = useSelector(selectOverlay)
    const [data, setData] = useState();
    const [ error, setError ] = useState();
    const cancelHandler = () => {
        dispatch(toggleOverlay(false))
    }
    const deleteHandler = async() => {
        try {
            const response = await fetch('localhost:3001/comments/:id/replies/:replieId',{
                method: 'DELETE',
                headers: {
                    'Content-Type': 'aplication/json'
                },

            })
            const responseData = await response.json();
				setData(responseData);
				//console.log("response",responseData);
				if (!response.ok) {
					throw new Error(responseData.message);
				}
        }catch(err) {
                setError(err.message || 'Something went wrong');
				console.log(err);
        }
        console.log('delete')
    }

	return (
		<div className="rounded-lg p-6 bg-white z-40 bg-opacity-100">
			<h2 className="text-xl text-dark-blue mb-4 font-medium">Delete comment</h2>
			<p className="font-normal text-base text-grayish-blue mb-4">
				Are you sure you want to delete this comment? This will remove the comment and canâ€™t be undone.
			</p>
            <div className="flex justify-between items-center">
                <button className="rounded-lg bg-grayish-blue text-white uppercase pt-3 pb-3 pl-5 pr-5" onClick={cancelHandler}>No, Cancel</button>
                <button className="rounded-lg bg-soft-red text-white uppercase pt-3 pb-3 pl-5 pr-5" onClick={deleteHandler}>Yes, Delete</button>
            </div>
		</div>
	);
}

export default Delete;
